/*!

=========================================================
* Black Dashboard React v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/black-dashboard-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/black-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";

class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = {email: '', password: ''};

    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleEmailChange(event) {
    this.setState({email: event.target.value});
  }

  handlePasswordChange(event) {
    this.setState({password: event.target.value});
  }

  handleSubmit(event) {
    // add entity - POST
    event.preventDefault();
    // creates entity
    fetch("https://localhost:5001/api/account/login", {
      "method": "POST",
      "headers": {
        "x-rapidapi-host": "fairestdb.p.rapidapi.com",
        "x-rapidapi-key": "API_KEY",
        "content-type": "application/json",
        "accept": "application/json"
      },
      "body": JSON.stringify({
        Email: this.state.email,
        Password: this.state.password
      })
    })
    .then(response => response.json())
    .then(response => {
      localStorage.setItem('token', response.data);
    })
    .catch(err => {
      console.log(err);
    });
  }

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="8">
              <Card>
                <CardHeader>
                  <h5 className="title text-center">Login</h5>
                </CardHeader>
                <CardBody>
                  <Form onSubmit={this.handleSubmit}>
                    <Row>
                      <Col className="px-md-3" md="12">
                        <FormGroup>
                          <label>E-mail</label>
                          <Input
                            placeholder="E-mail"
                            type="text"
                            onChange={this.handleEmailChange}
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col className="px-md-3" md="12">
                        <FormGroup>
                          <label>Senha</label>
                          <Input
                            placeholder="Senha"
                            type="password"
                            onChange={this.handlePasswordChange}
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col className="text-center pb-md-3">
                        <Button className="btn-fill" color="primary" type="submit">
                          Salvar
                        </Button>
                      </Col>
                      <Col className="text-center pb-md-3">
                        <a href="/user/register">Registrar novo usuário</a>
                      </Col>
                    </Row>
                    <Row>
                      <Col>
                        <div className="button-container text-center">
                          <Button className="btn-icon btn-round mx-md-3" color="facebook">
                            <i className="fab fa-facebook" />
                          </Button>
                          <Button className="btn-icon btn-round mx-md-3" color="google">
                            <i className="fab fa-google-plus" />
                          </Button>
                          <Button className="btn-icon btn-round mx-md-3" color="google">
                            <i className="fab fa-instagram" />
                          </Button>
                        </div>
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter className="text-center">                  
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default Login;
