/*!

=========================================================
* Black Dashboard React v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/black-dashboard-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/black-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import NotificationAlert from "react-notification-alert";
import api from '../../services/api';

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardText,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";

class UpdateUser extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      surname: '',
      email: '',
      username: '',
      password: '',
      confirmPassword: ''
    };

    this.messages = [];

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleCPFChange = this.handleCPFChange.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);

    console.log(this.props.match.params.id);
  }

  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleCPFChange(event) {
    this.setState({surname: event.target.value});
  }

  validateUser(){
    this.messages = [];

    if(!this.state.name){
      this.messages.push("Nome não preenchido.");
    }

    if(!this.state.cpf){
      this.messages.push("CPF não preenchido.");
    }

    if(this.messages.length > 0){
      return false;
    }

    return true;
  };

  async handleSubmit(event) {
    // add entity - POST
    event.preventDefault();


    if(!this.validateUser()){
      var options = {};
      var message = '';

      for(var i = 0; i < this.messages.length; i++){
        message = message +  this.messages[i] + '<br>';
        console.log(message);
      }

      options = {
        place: "tr",
        message: (message),
        type: "warning",
        icon: "tim-icons icon-bell-55",
        autoDismiss: 7
      };

      this.refs.notificationAlert.notificationAlert(options);

      return false;
    }

    const payload = {
      Name: this.state.name,
      CPF: this.state.cpf
    };

    const response = await api.put("user/", payload)
    .then(response => response.json())
    .then(response => {
      console.log(response)
    })
    .catch(err => {
      console.log(err);
    });
  }

  componentDidMount() {

    var options = {};

    const response = api.get("user/" + 5)
    .then(response => {
      this.setState({ users: response.data });

      if(this.state.users.length === 0){
        options = {
          place: "tr",
          message: "Não existem usuários cadastrados.",
          type: "warning",
          icon: "tim-icons icon-bell-55",
          autoDismiss: 7
        };

        this.refs.notificationAlert.notificationAlert(options);
      }

    })
    .catch(err => {
      console.log(err);

      options = {
        place: "tr",
        message: "Não existem usuários cadastrados.",
        type: "warning",
        icon: "tim-icons icon-bell-55",
        autoDismiss: 7
      };

      this.refs.notificationAlert.notificationAlert(options);

    });
  }

  componentWillUnmount() {
    this.serverRequest.abort();
  }


  render() {
    return (
      <>
        <div className="content">
          <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Row>
            <Col md="10">
              <Card>
                <CardHeader>
                  <h5 className="title">Atualizar perfil</h5>
                </CardHeader>
                <CardBody>
                  <Form onSubmit={this.handleSubmit}>
                    <Row>
                      <Col className="pr-md-1" md="6">
                        <FormGroup>
                          <label>Nome</label>
                          <Input                          
                            onChange={this.handleNameChange}
                            placeholder="Nome"
                            type="text"
                          />
                        </FormGroup>
                      </Col>
                      <Col className="pl-md-1" md="6">
                        <FormGroup>
                          <label>CPF</label>
                          <Input
                            onChange={this.handleCPFChange}
                            placeholder="CPF"
                            type="text"
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col md="12" className="text-center">
                        <Button className="btn-fill" color="primary" type="submit">
                          Salvar
                        </Button>
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter>
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default UpdateUser;
