/*!

=========================================================
* Black Dashboard React v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/black-dashboard-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/black-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import {RegisterUser} from '/RegisterUser';

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";

import {
  BrowserRouter as Router,
  Link,
  Route,
  Switch,
} from 'react-router-dom';

class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert("alert");
    // add entity - POST
    event.preventDefault();
    // creates entity
    // https://fairestdb.p.rapidapi.com/friend/friendModel
    fetch("https://localhost:5001/api/account/login", {
      "method": "POST",
      "headers": {
        "x-rapidapi-host": "fairestdb.p.rapidapi.com",
        "x-rapidapi-key": "API_KEY",
        "content-type": "application/json",
        "accept": "application/json"
      },
      "body": JSON.stringify({
        Email: this.state.email,
        Password: this.state.password
      })
    })
    .then(response => response.json())
    .then(response => {
      console.log(response)
    })
    .catch(err => {
      console.log(err);
    });
  }

  handleNewUserClick(e) {
    e.preventDefault();
    this.context.router.history.push('/user/register');
  }

  render() {
    return (
      <>
        <div className="content">
          <Row>
            <Col md="8">
              <Card>
                <CardHeader>
                  <h5 className="title text-center">Login</h5>
                </CardHeader>
                <CardBody>
                  <Form onSubmit={this.handleSubmit}>
                    <Row>
                      <Col className="px-md-3" md="12">
                        <FormGroup>
                          <label>E-mail</label>
                          <Input
                            defaultValue=""
                            placeholder="E-mail"
                            type="text"
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col className="px-md-3" md="12">
                        <FormGroup>
                          <label>Senha</label>
                          <Input
                            defaultValue=""
                            placeholder="Senha"
                            type="password"
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col className="text-center pb-md-3">
                        <Button className="btn-fill" color="primary" type="submit">
                          Salvar
                        </Button>
                      </Col>
                      <Col className="text-center pb-md-3">
                        <nav>
                          <Link to="/user/register">Registrar</Link>
                        </nav>
                        <Route
                            path="/user/register"
                            component={RegisterUser} 
                        />
                      </Col>
                    </Row>
                    <Row>
                      <Col>
                        <div className="button-container text-center">
                          <Button className="btn-icon btn-round mx-md-3" color="facebook">
                            <i className="fab fa-facebook" />
                          </Button>
                          <Button className="btn-icon btn-round mx-md-3" color="google">
                            <i className="fab fa-google-plus" />
                          </Button>
                          <Button className="btn-icon btn-round mx-md-3" color="google">
                            <i className="fab fa-instagram" />
                          </Button>
                        </div>
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter className="text-center">                  
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default Login;
