/*!

=========================================================
* Black Dashboard React v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/black-dashboard-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/black-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import NotificationAlert from "react-notification-alert";
import api from '../../services/api';

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardText,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";

class ListUsers extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      users: []
    };

    this.messages = [];

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleSurnameChange = this.handleSurnameChange.bind(this);
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handleUsernameChange = this.handleUsernameChange.bind(this);
    this.handleConfirmPasswordChange = this.handleConfirmPasswordChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);

  }

  componentDidMount() {
    const response = api.get("user")
    .then(response => {
      console.log(response);
      users = response.data;
    })
    .then(response => {
      console.log(response)
    })
    .catch(err => {
      console.log(err);
    });
  }

  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleSurnameChange(event) {
    this.setState({surname: event.target.value});
  }

  handleEmailChange(event) {
    this.setState({email: event.target.value});
  }

  handleUsernameChange(event) {
    this.setState({username: event.target.value});
  }

  handleConfirmPasswordChange(event) {
    this.setState({confirmPassword: event.target.value});
  }

  handlePasswordChange(event) {
    this.setState({password: event.target.value});
  }


  validateUser(){
    this.messages = [];

    if(!this.state.name){
      this.messages.push("Nome não preenchido.");
    }

    if(!this.state.surname){
      this.messages.push("Sobrenome não preenchido.");
    }

    if(!this.state.email){
      this.messages.push("Email não preenchido.");
    }

    if(!this.state.username){
      this.messages.push("Username não preenchido.");
    }

    if(!this.state.password){
      this.messages.push("Senha não preenchida");
    }

    if(!this.state.confirmPassword){
      this.messages.push("Confirmação de senha não preenchida");
    }

    if(this.state.password !== this.state.confirmPassword){
      this.messages.push("Senha e confirmação de senha não conferem.");
    }

    if(this.messages.length > 0){
      return false;
    }

    return true;
  };

  async handleSubmit(event) {
    // add entity - POST
    event.preventDefault();


    if(!this.validateUser()){
      var options = {};
      var message = '';

      for(var i = 0; i < this.messages.length; i++){
        message = message +  this.messages[i] + '<br>';
        console.log(message);
      }

      options = {
        place: "tr",
        message: (message),
        type: "warning",
        icon: "tim-icons icon-bell-55",
        autoDismiss: 7
      };

      this.refs.notificationAlert.notificationAlert(options);

      return false;
    }

    const payload = {
      Name: this.state.name,
      Surname: this.state.surname,
      Username: this.state.username,
      Email: this.state.email,
      Password: this.state.password,
      PasswordConfirm: this.state.confirmPassword
    };

    const response = await api.post("account/register", payload)
    .then(response => response.json())
    .then(response => {
      console.log(response)
    })
    .catch(err => {
      console.log(err);
    });
  }

  render() {
    return (
      <>
        <div className="content">
          <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Row>
            <Col md="10">
              <Card>
                <CardHeader>
                  <h5 className="title">Criar perfil</h5>
                </CardHeader>
                <CardBody>
                  <Form onSubmit={this.handleSubmit}>
                    <Row>
                      <Col className="pr-md-1" md="6">
                        <FormGroup>
                          <label>First Name</label>
                          <Input                          
                            onChange={this.handleNameChange}
                            placeholder="Nome"
                            type="text"
                          />
                        </FormGroup>
                      </Col>
                      <Col className="pl-md-1" md="6">
                        <FormGroup>
                          <label>Last Name</label>
                          <Input
                            onChange={this.handleSurnameChange}
                            placeholder="Last Name"
                            type="text"
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>                      
                      <Col className="pr-md-1" md="6">
                        <FormGroup>
                          <label>Username</label>
                          <Input
                            onChange={this.handleUsernameChange}
                            placeholder="Username"
                            type="text"
                          />
                        </FormGroup>
                      </Col>
                      <Col className="pl-md-1" md="6">
                        <FormGroup>
                          <label htmlFor="exampleInputEmail1">
                            Email address
                          </label>
                          <Input 
                            onChange={this.handleEmailChange} 
                            placeholder="seu@email.com" 
                            type="email" />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col className="pr-md-1" md="6">
                        <FormGroup>
                          <label>Senha</label>
                          <Input
                            onChange={this.handlePasswordChange}
                            placeholder="Senha"
                            type="password"
                          />
                        </FormGroup>
                      </Col>
                      <Col className="pl-md-1" md="6">
                        <FormGroup>
                          <label>Confirmação de senha</label>
                          <Input
                            onChange={this.handleConfirmPasswordChange}
                            placeholder="Confirmação de senha"
                            type="password"
                          />
                        </FormGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col md="12" className="text-center">
                        <Button className="btn-fill" color="primary" type="submit">
                          Save
                        </Button>
                      </Col>
                    </Row>
                  </Form>
                </CardBody>
                <CardFooter>
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default ListUsers;
