/*!

=========================================================
* Black Dashboard React v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/black-dashboard-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/black-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import NotificationAlert from "react-notification-alert";
import api from '../../services/api';

// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardText,
  FormGroup,
  Form,
  Input,
  Row,
  Col
} from "reactstrap";

class UserByCpf extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      cpf: '',
      street: '',
      number: ''
    };

    this.messages = [];

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleCPFChange = this.handleCPFChange.bind(this);
    this.handleStreetChange = this.handleStreetChange.bind(this);
    this.handleNumberChange = this.handleNumberChange.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleCPFChange(event) {
    this.setState({cpf: event.target.value});
  }

  handleStreetChange(event) {
    this.setState({street: event.target.value});
  }

  handleNumberChange(event) {
    this.setState({number: event.target.value});
  }

  validateUser(){
    this.messages = [];

    if(!this.state.name){
      this.messages.push("Nome não preenchido.");
    }

    if(!this.state.cpf){
      this.messages.push("CPF não preenchido.");
    }

    if(!this.state.street){
      this.messages.push("Logradouro não preenchido.");
    }

    if(!this.state.number){
      this.messages.push("Número não preenchido.");
    }

    if(this.messages.length > 0){
      return false;
    }

    return true;
  };

  async handleSubmit(event) {
    // add entity - POST
    event.preventDefault();


    if(!this.validateUser()){
      var options = {};
      var message = '';

      for(var i = 0; i < this.messages.length; i++){
        message = message +  this.messages[i] + '<br>';
        console.log(message);
      }

      options = {
        place: "tr",
        message: (message),
        type: "warning",
        icon: "tim-icons icon-bell-55",
        autoDismiss: 7
      };

      this.refs.notificationAlert.notificationAlert(options);

      return false;
    }

    const payload = {
      Name: this.state.name,
      CPF: this.state.cpf,
      Address: {
        Street: this.state.street,
        Number: this.state.number
      }
    };

    const response = await api.post("user", payload)
    .then(response => {
      console.log(response);

      this.setState({name: ""});
      this.setState({cpf: ""});
      this.setState({street: ""});
      this.setState({number: ""});

      options = {
        place: "tr",
        message: "Usuário criado com sucesso.",
        type: "success",
        icon: "tim-icons icon-bell-55",
        autoDismiss: 7
      };

      this.refs.notificationAlert.notificationAlert(options);


    })
    .catch(err => {
      console.log(err);
    });
  }

  render() {

    let userName = this.state.name;
    let userCpf = this.state.cpf;
    let userAddressNumber = this.state.number;
    let userAddressStreet = this.state.street;

    return (
      <>
        <div className="content">
          <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Row>
            <Col md="10">
              <Card>
                <CardHeader>
                  <h5 className="title">Criar usuario</h5>
                </CardHeader>
                <CardBody>
                <Row>
                    <Col className="pl-md-1" md="12">
                      <FormGroup>
                        <label>CPF</label>
                        <Input
                          placeholder="CPF"
                          type="text"
                          onChange={this.handleCPFChange}
                        />
                        <Button onClick={() => this.handleGetUser(userCpf)} className="btn-fill mr-4" color="primary" type="button">
                              Buscar
                            </Button>
                      </FormGroup>
                    </Col>
                  </Row>
                    <Row>
                      <Col className="pr-md-1" md="6">
                          <label>Nome</label>
                          <Input                          
                            placeholder="Nome"
                            type="text"
                            value={userName}
                          />
                      </Col>
                      <Col className="pl-md-1" md="6">
                          <label>CPF</label>
                          <Input
                            placeholder="CPF"
                            type="text"
                            value={userCpf}
                          />
                      </Col>
                    </Row>
                    <Row>
                      <Col className="pr-md-1" md="6">
                          <label>Logradouro</label>
                          <Input
                            placeholder="Logradouro"
                            type="text"
                            value={userAddressStreet}
                          />
                      </Col>
                      <Col className="pl-md-1" md="6">
                          <label>Número</label>
                          <Input
                            placeholder="Número"
                            type="text"
                            value={userAddressNumber}
                          />
                      </Col>
                    </Row>
                </CardBody>
                <CardFooter>
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </>
    );
  }
}

export default UserByCpf;
